contracts/interfaces/IRewardVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IRewardVault
 * @notice Interface for the MicroSave reward points system
 * @dev Manages point allocation and claiming for consistent savers
 */
interface IRewardVault {
    // Events
    event PointsAwarded(address indexed user, uint256 amount, uint16 streak);
    event PointsClaimed(address indexed user, uint256 amount);

    // Errors
    error OnlyPool();
    error InsufficientPoints();
    error InvalidAmount();

    /**
     * @notice Called by SavingsPool on each deposit day
     * @param user User who made the deposit
     * @param streak Current streak days
     */
    function onDepositDay(address user, uint16 streak) external;

    /**
     * @notice Claim accumulated points (burns them)
     * @param amount Amount of points to claim
     */
    function claimPoints(uint256 amount) external;

    /**
     * @notice Get user's current point balance
     * @param user User address
     * @return Current point balance
     */
    function getPoints(address user) external view returns (uint256);

    // State variables
    function points(address user) external view returns (uint256);
    function savingsPool() external view returns (address);
}

contracts/interfaces/ISavingsPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./IScoreRegistry.sol";
import "./IRewardVault.sol";

/**
 * @title ISavingsPool
 * @notice Interface for the core MicroSave savings pool contract
 * @dev Handles stablecoin deposits/withdrawals and integrates with scoring system
 */
interface ISavingsPool {
    // Events
    event Deposited(address indexed user, uint256 amount, uint256 netAmount, uint40 dayIndex);
    event Withdrawn(address indexed user, uint256 amount);
    event ScoreUpdated(address indexed user, uint16 newScore, uint16 streakDays, uint8 freq30);
    event FeeUpdated(uint16 depositFeeBps);
    event TreasuryUpdated(address treasury);

    // Errors
    error InsufficientAmount();
    error InsufficientBalance();
    error InvalidToken();
    error NoPermitSupport();
    error ZeroAddress();
    error ExcessiveFee();

    /**
     * @notice Deposit stablecoin into the savings pool
     * @param amount Amount to deposit
     */
    function deposit(uint256 amount) external;

    /**
     * @notice Deposit with EIP-2612 permit (gasless approval)
     * @param amount Amount to deposit
     * @param deadline Permit deadline
     * @param v Signature parameter
     * @param r Signature parameter
     * @param s Signature parameter
     */
    function permitDeposit(
        uint256 amount,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    /**
     * @notice Withdraw stablecoin from the savings pool
     * @param amount Amount to withdraw
     */
    function withdraw(uint256 amount) external;

    /**
     * @notice Set deposit fee (only owner)
     * @param depositFeeBps Fee in basis points (0-100)
     */
    function setFeeBps(uint16 depositFeeBps) external;

    /**
     * @notice Set treasury address (only owner)
     * @param treasury New treasury address
     */
    function setTreasury(address treasury) external;

    // View functions
    function balances(address user) external view returns (uint256);
    function totalDeposits() external view returns (uint256);
    function STABLE() external view returns (IERC20);
    function scoreRegistry() external view returns (IScoreRegistry);
    function rewardVault() external view returns (IRewardVault);
    function treasury() external view returns (address);
    function depositFeeBps() external view returns (uint16);
}

contracts/interfaces/IScoreRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IScoreRegistry
 * @notice Interface for the Savings Consistency Score (SCS) registry
 * @dev Calculates and stores user scores based on deposit frequency and streaks
 */
interface IScoreRegistry {
    // Events
    event ScoreSet(address indexed user, uint16 score, uint16 streak, uint8 freq30);

    // Errors
    error OnlyPool();
    error InvalidUser();

    /**
     * @notice Update user's savings consistency score
     * @param user User address
     * @param userDaysFromPool Array of deposit day indices from the pool
     * @return newScore Updated score (0-100)
     * @return newStreak Current streak in days
     * @return freq30 Frequency in last 30 days
     */
    function updateScore(address user, uint40[] calldata userDaysFromPool)
        external
        returns (uint16 newScore, uint16 newStreak, uint8 freq30);

    /**
     * @notice Get user's current score
     * @param user User address
     * @return Current savings consistency score
     */
    function getUserScore(address user) external view returns (uint16);

    /**
     * @notice Get user's current streak
     * @param user User address
     * @return Current streak in days
     */
    function getUserStreak(address user) external view returns (uint16);

    /**
     * @notice Get user's frequency in last 30 days
     * @param user User address
     * @return Deposit frequency in last 30 days
     */
    function getUserFreq30(address user) external view returns (uint8);

    /**
     * @notice Get comprehensive user stats
     * @param user User address
     * @return score Current score
     * @return streak Current streak
     * @return freq30 Frequency in last 30 days
     */
    function getUserStats(address user) external view returns (uint16 score, uint16 streak, uint8 freq30);

    // State variables
    function score(address user) external view returns (uint16);
    function streakDays(address user) external view returns (uint16);
    function savingsPool() external view returns (address);
}

contracts/mocks/MockERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MockERC20
 * @notice Mock ERC20 token with permit functionality for testing MicroSave
 * @dev Includes mint function for easy test setup
 */
contract MockERC20 is ERC20, ERC20Permit, Ownable {
    uint8 private _decimals;

    /**
     * @notice Constructor
     * @param name Token name
     * @param symbol Token symbol
     * @param decimals_ Token decimals
     * @param owner Token owner
     */
    constructor(
        string memory name,
        string memory symbol,
        uint8 decimals_,
        address owner
    ) ERC20(name, symbol) ERC20Permit(name) Ownable(owner) {
        _decimals = decimals_;
    }

    /**
     * @notice Override decimals function
     * @return Number of decimals
     */
    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    /**
     * @notice Mint tokens to an address
     * @param to Address to mint to
     * @param amount Amount to mint
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @notice Mint tokens to multiple addresses
     * @param recipients Array of recipient addresses
     * @param amounts Array of amounts to mint
     */
    function batchMint(address[] calldata recipients, uint256[] calldata amounts) external onlyOwner {
        require(recipients.length == amounts.length, "Arrays length mismatch");

        for (uint256 i = 0; i < recipients.length; i++) {
            _mint(recipients[i], amounts[i]);
        }
    }

    /**
     * @notice Burn tokens from an address (for testing)
     * @param from Address to burn from
     * @param amount Amount to burn
     */
    function burn(address from, uint256 amount) external onlyOwner {
        _burn(from, amount);
    }

    /**
     * @notice Faucet function for easy testing (anyone can call)
     * @param amount Amount to mint to caller
     */
    function faucet(uint256 amount) external {
        _mint(msg.sender, amount);
    }
}

contracts/RewardVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IRewardVault.sol";

/**
 * @title RewardVault
 * @notice Manages reward points for consistent MicroSave users
 * @dev Awards 5 points per deposit day + 20 bonus points per 7-day streak milestone
 */
contract RewardVault is IRewardVault, Ownable {
    /// @notice Points awarded per deposit day
    uint256 public constant POINTS_PER_DEPOSIT = 5;

    /// @notice Bonus points awarded per streak milestone
    uint256 public constant STREAK_BONUS_POINTS = 20;

    /// @notice Streak milestone interval (every 7 days)
    uint256 public constant STREAK_MILESTONE = 7;

    /// @notice Address of the authorized savings pool
    address public immutable savingsPool;

    /// @notice User point balances
    mapping(address => uint256) public points;

    /**
     * @notice Modifier to restrict access to savings pool only
     */
    modifier onlyPool() {
        if (msg.sender != savingsPool) revert OnlyPool();
        _;
    }

    /**
     * @notice Constructor
     * @param _savingsPool Address of the savings pool contract
     * @param _owner Address of the contract owner
     */
    constructor(address _savingsPool, address _owner) Ownable(_owner) {
        if (_savingsPool == address(0)) revert OnlyPool();
        savingsPool = _savingsPool;
    }

    /**
     * @notice Called by SavingsPool on each deposit day
     * @param user User who made the deposit
     * @param streak Current streak days
     */
    function onDepositDay(address user, uint16 streak) external onlyPool {
        if (user == address(0)) revert InvalidAmount();

        // Award base points for deposit day
        uint256 pointsToAward = POINTS_PER_DEPOSIT;

        // Award streak bonus if milestone reached
        if (streak > 0 && streak % STREAK_MILESTONE == 0) {
            pointsToAward += STREAK_BONUS_POINTS;
        }

        // Update user points
        points[user] += pointsToAward;

        emit PointsAwarded(user, pointsToAward, streak);
    }

    /**
     * @notice Claim accumulated points (burns them)
     * @param amount Amount of points to claim
     */
    function claimPoints(uint256 amount) external {
        if (amount == 0) revert InvalidAmount();
        if (points[msg.sender] < amount) revert InsufficientPoints();

        // Burn points (no token transfer in MVP)
        unchecked {
            points[msg.sender] -= amount;
        }

        emit PointsClaimed(msg.sender, amount);
    }

    /**
     * @notice Get user's current point balance
     * @param user User address
     * @return Current point balance
     */
    function getPoints(address user) external view returns (uint256) {
        return points[user];
    }
}

contracts/SavingsPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Permit.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./interfaces/ISavingsPool.sol";
import "./interfaces/IScoreRegistry.sol";
import "./interfaces/IRewardVault.sol";

/**
 * @title SavingsPool
 * @notice Core MicroSave savings pool for stablecoin deposits and withdrawals
 * @dev Integrates with ScoreRegistry for SCS tracking and RewardVault for points
 */
contract SavingsPool is ISavingsPool, Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    /// @notice Maximum fee in basis points (1% = 100 bps)
    uint16 public constant MAX_FEE_BPS = 100;

    /// @notice The stablecoin token used for deposits/withdrawals
    IERC20 public immutable STABLE;

    /// @notice Score registry contract for SCS calculations
    IScoreRegistry public immutable scoreRegistry;

    /// @notice Reward vault contract for points (can be zero address)
    IRewardVault public immutable rewardVault;

    /// @notice Treasury address for fees (can be zero address)
    address public treasury;

    /// @notice Deposit fee in basis points (0-100)
    uint16 public depositFeeBps;

    /// @notice User balances in the pool
    mapping(address => uint256) public balances;

    /// @notice Total deposits in the pool
    uint256 public totalDeposits;

    /// @notice User deposit days (day indices: block.timestamp / 1 days)
    mapping(address => uint40[]) private _depositDays;

    /// @notice Last scored day for each user (for edge case handling)
    mapping(address => uint40) public lastScoredDay;

    /**
     * @notice Constructor
     * @param _stablecoin Address of the stablecoin token
     * @param _scoreRegistry Address of the score registry
     * @param _rewardVault Address of the reward vault (can be zero)
     * @param _treasury Address of the treasury (can be zero)
     * @param _depositFeeBps Initial deposit fee in basis points
     * @param _owner Address of the contract owner
     */
    constructor(
        address _stablecoin,
        address _scoreRegistry,
        address _rewardVault,
        address _treasury,
        uint16 _depositFeeBps,
        address _owner
    ) Ownable(_owner) {
        if (_stablecoin == address(0)) revert InvalidToken();
        if (_scoreRegistry == address(0)) revert ZeroAddress();
        if (_depositFeeBps > MAX_FEE_BPS) revert ExcessiveFee();

        STABLE = IERC20(_stablecoin);
        scoreRegistry = IScoreRegistry(_scoreRegistry);
        rewardVault = IRewardVault(_rewardVault);
        treasury = _treasury;
        depositFeeBps = _depositFeeBps;
    }

    /**
     * @notice Deposit stablecoin into the savings pool
     * @param amount Amount to deposit
     */
    function deposit(uint256 amount) external nonReentrant whenNotPaused {
        if (amount == 0) revert InsufficientAmount();

        address user = msg.sender;
        uint40 today = uint40(block.timestamp / 1 days);

        // Transfer tokens from user
        STABLE.safeTransferFrom(user, address(this), amount);

        // Calculate fee and net amount
        uint256 fee = 0;
        if (depositFeeBps > 0 && treasury != address(0)) {
            fee = (amount * depositFeeBps) / 10000;
            if (fee > 0) {
                STABLE.safeTransfer(treasury, fee);
            }
        }

        uint256 netAmount = amount - fee;

        // Update balances
        balances[user] += netAmount;
        totalDeposits += netAmount;

        // Record deposit day (only once per day per user)
        bool isNewDay = _recordDepositDay(user, today);

        // Update score if it's a new deposit day
        uint16 newScore = 0;
        uint16 streakDays = 0;
        uint8 freq30 = 0;

        if (isNewDay) {
            (newScore, streakDays, freq30) = scoreRegistry.updateScore(user, _depositDays[user]);
            lastScoredDay[user] = today;

            // Award points if reward vault is set
            if (address(rewardVault) != address(0)) {
                try rewardVault.onDepositDay(user, streakDays) {
                    // Points awarded successfully
                } catch {
                    // Continue even if reward fails
                }
            }

            emit ScoreUpdated(user, newScore, streakDays, freq30);
        }

        emit Deposited(user, amount, netAmount, today);
    }

    /**
     * @notice Deposit with EIP-2612 permit (gasless approval)
     * @param amount Amount to deposit
     * @param deadline Permit deadline
     * @param v Signature parameter
     * @param r Signature parameter
     * @param s Signature parameter
     */
    function permitDeposit(
        uint256 amount,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external nonReentrant whenNotPaused {
        if (amount == 0) revert InsufficientAmount();

        // Try to call permit - will revert if not supported
        try IERC20Permit(address(STABLE)).permit(
            msg.sender,
            address(this),
            amount,
            deadline,
            v,
            r,
            s
        ) {
            // Permit successful, proceed with deposit
            _executeDeposit(msg.sender, amount);
        } catch {
            revert NoPermitSupport();
        }
    }

    /**
     * @notice Internal function to execute deposit logic
     * @param user User address
     * @param amount Amount to deposit
     */
    function _executeDeposit(address user, uint256 amount) private {
        uint40 today = uint40(block.timestamp / 1 days);

        // Transfer tokens from user
        STABLE.safeTransferFrom(user, address(this), amount);

        // Calculate fee and net amount
        uint256 fee = 0;
        if (depositFeeBps > 0 && treasury != address(0)) {
            fee = (amount * depositFeeBps) / 10000;
            if (fee > 0) {
                STABLE.safeTransfer(treasury, fee);
            }
        }

        uint256 netAmount = amount - fee;

        // Update balances
        balances[user] += netAmount;
        totalDeposits += netAmount;

        // Record deposit day (only once per day per user)
        bool isNewDay = _recordDepositDay(user, today);

        // Update score if it's a new deposit day
        if (isNewDay) {
            (uint16 newScore, uint16 streakDays, uint8 freq30) = 
                scoreRegistry.updateScore(user, _depositDays[user]);
            lastScoredDay[user] = today;

            // Award points if reward vault is set
            if (address(rewardVault) != address(0)) {
                try rewardVault.onDepositDay(user, streakDays) {
                    // Points awarded successfully
                } catch {
                    // Continue even if reward fails
                }
            }

            emit ScoreUpdated(user, newScore, streakDays, freq30);
        }

        emit Deposited(user, amount, netAmount, today);
    }

    /**
     * @notice Withdraw stablecoin from the savings pool
     * @param amount Amount to withdraw
     */
    function withdraw(uint256 amount) external nonReentrant whenNotPaused {
        if (amount == 0) revert InsufficientAmount();
        if (balances[msg.sender] < amount) revert InsufficientBalance();

        address user = msg.sender;

        // Update balances
        unchecked {
            balances[user] -= amount;
            totalDeposits -= amount;
        }

        // Transfer tokens to user
        STABLE.safeTransfer(user, amount);

        emit Withdrawn(user, amount);
    }

    /**
     * @notice Record deposit day for user (prevents duplicates)
     * @param user User address
     * @param dayIndex Day index to record
     * @return isNewDay True if this is a new deposit day
     */
    function _recordDepositDay(address user, uint40 dayIndex) private returns (bool isNewDay) {
        uint40[] storage userDays = _depositDays[user];

        // Check if day already recorded (assume sorted array)
        if (userDays.length > 0 && userDays[userDays.length - 1] == dayIndex) {
            return false; // Same day, not new
        }

        // Add new day
        userDays.push(dayIndex);
        return true;
    }

    /**
     * @notice Get user's deposit days (view function)
     * @param user User address
     * @return Array of deposit day indices
     */
    function getUserDepositDays(address user) external view returns (uint40[] memory) {
        return _depositDays[user];
    }

    /**
     * @notice Get user's deposit count
     * @param user User address
     * @return Number of unique deposit days
     */
    function getUserDepositCount(address user) external view returns (uint256) {
        return _depositDays[user].length;
    }

    /**
     * @notice Set deposit fee (only owner)
     * @param _depositFeeBps Fee in basis points (0-100)
     */
    function setFeeBps(uint16 _depositFeeBps) external onlyOwner {
        if (_depositFeeBps > MAX_FEE_BPS) revert ExcessiveFee();
        depositFeeBps = _depositFeeBps;
        emit FeeUpdated(_depositFeeBps);
    }

    /**
     * @notice Set treasury address (only owner)
     * @param _treasury New treasury address
     */
    function setTreasury(address _treasury) external onlyOwner {
        treasury = _treasury;
        emit TreasuryUpdated(_treasury);
    }

    /**
     * @notice Pause the contract (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the contract (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Emergency withdraw for owner (only when paused)
     * @param token Token to withdraw
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner whenPaused {
        IERC20(token).safeTransfer(owner(), amount);
    }
}

